import React from 'react';
import { SortableItem } from '@dnd-kit/sortable';
import '../styles/card.css';

const Card = ({ card }) => {
  return (
    <SortableItem id={card.id}>
      <div className="card">
        <div className="card-title">{card.title}</div>
        <div className="card-description">{card.description}</div>
      </div>
    </SortableItem>
  );
};

export default Card;
