import React, { useState } from 'react';
import Timeline from './Timeline';
import Card from './Card';

function Board() {
  const [cards, setCards] = useState([]);
  const [newEventDescription, setNewEventDescription] = useState('');

  const addCard = (description) => {
    const newCard = {
      id: Date.now(),
      date: new Date().toLocaleDateString(),
      description,
    };
    setCards([...cards, newCard]);
    setNewEventDescription(''); // Clear the input after adding the card
  };

  return (
    <div>
      <h2>Project Board</h2>
      <input
        type="text"
        value={newEventDescription}
        onChange={(e) => setNewEventDescription(e.target.value)}
        placeholder="Enter event description"
      />
      <button onClick={() => addCard(newEventDescription)}>Add New Event</button>
      <Timeline events={cards} addCard={addCard} />
      <div className="cards-container">
        {cards.map((card) => (
          <Card key={card.id} event={card} />
        ))}
      </div>
    </div>
  );
}

export default Board;
