// Board.js
import React, { useState } from 'react';
import Timeline from './Timeline';
import Card from './Card';
import '../styles/board.css'; // Optional, if you have a custom style for the Board component

function Board() {
  const [cards, setCards] = useState([]);

  const addCard = (description) => {
    const newCard = {
      id: Date.now(),
      date: new Date().toLocaleDateString(),
      description,
    };
    setCards([...cards, newCard]);
  };

  return (
    <div className="container">
      <h1 className="center-align">Collaborative Project Management Tool</h1>
      <Timeline events={cards} addCard={addCard} />
      <div className="row">
        {cards.map((card) => (
          <div className="col s12 m6 l4" key={card.id}>
            <Card event={card} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Board;
