// Timeline.js
import React from 'react';
import '../styles/timeline.css'; // Ensure correct casing for the import

function Timeline({ events, addCard }) {
  const handleAddEventClick = () => {
    if (typeof addCard === 'function') {
      addCard('New Event');
    } else {
      console.error('addCard is not a function');
    }
  };

  return (
    <div className="timeline container">
      <h2 className="center-align">Project Timeline</h2>
      <ul className="collection">
        {events.map((event, index) => (
          <li className="collection-item avatar" key={index}>
            <i className="material-icons circle blue">event</i>
            <span className="title">{event.date}</span>
            <p>{event.description}</p>
          </li>
        ))}
      </ul>
      <div className="center-align">
        <button className="btn waves-effect waves-light green" onClick={handleAddEventClick}>
          Add New Event
        </button>
      </div>
    </div>
  );
}

export default Timeline;
