// src/components/Board.js
import React, { useState } from 'react';
import Column from './Column';
import EventForm from './EventForm';
import { DndContext } from '@dnd-kit/core';
import { SortableContext, verticalListSortingStrategy } from '@dnd-kit/sortable';
import '../styles/board.css';

const initialColumns = {
  todos: { id: 'todos', title: 'To Do', items: [] },
  inProgress: { id: 'inProgress', title: 'In Progress', items: [] },
  completed: { id: 'completed', title: 'Completed', items: [] },
};

const Board = () => {
  const [columns, setColumns] = useState(initialColumns);

  const addCard = (card) => {
    console.log('Adding Card:', card); // Log the new card data
    setColumns((prevColumns) => {
      const updatedColumns = {
        ...prevColumns,
        todos: {
          ...prevColumns.todos,
          items: [...prevColumns.todos.items, card],
        },
      };
      console.log('Updated Columns after adding card:', updatedColumns); // Log the updated columns
      return updatedColumns;
    });
  };

  const handleDragEnd = (event) => {
    const { active, over } = event;

    if (!over) return;

    const activeColumn = findColumn(active.id);
    const overColumn = findColumn(over.id);

    if (!activeColumn || !overColumn) {
      console.error('Active or over column not found', { activeColumn, overColumn });
      return;
    }

    if (activeColumn === overColumn) {
      const newItems = arrayMove(
        columns[activeColumn].items,
        findIndex(activeColumn, active.id),
        findIndex(overColumn, over.id)
      );
      setColumns((prevColumns) => ({
        ...prevColumns,
        [activeColumn]: { ...prevColumns[activeColumn], items: newItems },
      }));
    } else {
      const newActiveItems = [...columns[activeColumn].items];
      const newOverItems = [...columns[overColumn].items];
      const [movedItem] = newActiveItems.splice(findIndex(activeColumn, active.id), 1);

      if (movedItem) {
        newOverItems.splice(findIndex(overColumn, over.id), 0, movedItem);

        setColumns((prevColumns) => ({
          ...prevColumns,
          [activeColumn]: { ...prevColumns[activeColumn], items: newActiveItems },
          [overColumn]: { ...prevColumns[overColumn], items: newOverItems },
        }));
      } else {
        console.error('Moved item not found in active column', { activeColumn, activeId: active.id });
      }
    }
  };

  const findColumn = (id) => {
    return Object.keys(columns).find((key) =>
      columns[key].items.some((item) => item.id === id)
    );
  };

  const findIndex = (columnId, cardId) => columns[columnId]?.items.findIndex((item) => item.id === cardId);

  return (
    <DndContext onDragEnd={handleDragEnd}>
      <div className="board">
        <div className="event-form-section">
          <EventForm onAddCard={addCard} />
        </div>
        <div className="columns">
          {Object.keys(columns).map((key) => (
            <SortableContext key={key} items={columns[key].items} strategy={verticalListSortingStrategy}>
              <Column column={columns[key]} />
            </SortableContext>
          ))}
        </div>
      </div>
    </DndContext>
  );
};

export default Board;
