// Card.js
import React from 'react';

function Card({ card }) {
  const handleDragStart = (e) => {
    e.dataTransfer.setData('cardId', card.id);
    e.dataTransfer.setData('fromColumnId', e.target.parentElement.id);
  };

  return (
    <div
      className="card"
      draggable
      onDragStart={handleDragStart}
      id={`card-${card.id}`}
    >
      {card.content}
    </div>
  );
}

export default Card;
