import React, { useState } from 'react';
import Timeline from './Timeline';
import Card from './Card';

function Board() {
  const [cards, setCards] = useState([]);

  const addCard = (description) => {
    const newCard = {
      id: Date.now(),
      date: new Date().toLocaleDateString(),
      description,
    };
    setCards([...cards, newCard]);
  };

  return (
    <div className="row">
      <div className="col s12 m6 l4">
        <div className="card blue-grey darken-1">
          <div className="card-content white-text">
            <span className="card-title">Welcome to Your Project Board</span>
            <p>Use this tool to manage your project collaboratively.</p>
          </div>
        </div>
      </div>
      <div className="col s12 m6 l8">
        <Timeline events={cards} addCard={addCard} />
      </div>
      <div className="col s12">
        {cards.map((card) => (
          <Card key={card.id} event={card} />
        ))}
      </div>
    </div>
  );
}

export default Board;
