// src/components/Column.js
import React from 'react';
import { useDrop } from 'react-dnd';
import SortableCard from './SortableCard';
import '../styles/column.css';

const Column = ({ column, moveCard }) => {
  const [, drop] = useDrop({
    accept: 'CARD',
    hover(item, monitor) {
      if (!ref.current) return;
      const dragIndex = item.index;
      const hoverIndex = index;

      if (dragIndex === hoverIndex) {
        return;
      }

      const hoverBoundingRect = ref.current.getBoundingClientRect();
      const hoverMiddleY = (hoverBoundingRect.bottom - hoverBoundingRect.top) / 2;
      const clientOffset = monitor.getClientOffset();
      const hoverClientY = clientOffset.y - hoverBoundingRect.top;

      if (dragIndex < hoverIndex && hoverClientY < hoverMiddleY) {
        return;
      }

      if (dragIndex > hoverIndex && hoverClientY > hoverMiddleY) {
        return;
      }

      moveCard(item.id, item.from, item.from, hoverIndex);
      item.index = hoverIndex;
    },
    drop(item) {
      if (item.from !== column.id) {
        moveCard(item.id, item.from, column.id);
        item.from = column.id;
      }
    },
  });

  return (
    <div ref={drop} className="column">
      <div className="column-title">{column.title}</div>
      <div className="column-content">
        {column.items.map((item, index) => (
          <SortableCard key={item.id} id={item.id} card={item} index={index} moveCard={moveCard} />
        ))}
      </div>
    </div>
  );
};

export default Column;
