// Board.js
import React, { useState } from 'react';
import Timeline from './Timeline';
import '../styles/style.css'; // Ensure your CSS file is imported

function Board() {
  const [events, setEvents] = useState([]);
  const [todos, setTodos] = useState([]);
  const [inProgress, setInProgress] = useState([]);
  const [completed, setCompleted] = useState([]);

  const addEvent = (event) => {
    const newEvent = { id: Date.now(), ...event };
    setEvents([...events, newEvent]);
  };

  const moveEvent = (eventId, fromList, toList) => {
    const updatedEvents = events.filter((event) => event.id !== eventId);

    switch (toList) {
      case 'todos':
        setTodos([...todos, { id: eventId, ...events.find((event) => event.id === eventId) }]);
        break;
      case 'inProgress':
        setInProgress([...inProgress, { id: eventId, ...events.find((event) => event.id === eventId) }]);
        break;
      case 'completed':
        setCompleted([...completed, { id: eventId, ...events.find((event) => event.id === eventId) }]);
        break;
      default:
        break;
    }

    setEvents(updatedEvents);
  };

  return (
    <div className="board">
      <div className="timeline-container">
        <h2>Timeline</h2>
        <Timeline events={events} addEvent={addEvent} />
      </div>

      <div className="kanban-container">
        <div className="kanban-column">
          <h2>To Do</h2>
          {todos.map((event) => (
            <div key={event.id} className="kanban-card">
              {event.description}
            </div>
          ))}
        </div>

        <div className="kanban-column">
          <h2>In Progress</h2>
          {inProgress.map((event) => (
            <div key={event.id} className="kanban-card">
              {event.description}
            </div>
          ))}
        </div>

        <div className="kanban-column">
          <h2>Completed</h2>
          {completed.map((event) => (
            <div key={event.id} className="kanban-card">
              {event.description}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Board;
