// Timeline.js
import React from 'react';
import './Timeline.css'; // Import the CSS file for styling

function Timeline({ events, addCard }) {
  const handleAddEventClick = () => {
    if (typeof addCard === 'function') {
      addCard('New Event'); // This could be enhanced to prompt user input
    } else {
      console.error('addCard is not a function');
    }
  };

  return (
    <div className="timeline">
      <h2 className="timeline-title">Project Timeline</h2>
      <div className="timeline-content">
        {events.map((event, index) => (
          <div className="timeline-event" key={index}>
            <div className="timeline-event-dot"></div>
            <div className="timeline-event-date">{event.date}</div>
            <div className="timeline-event-description">{event.description}</div>
          </div>
        ))}
      </div>
      <button className="add-event-button" onClick={handleAddEventClick}>
        Add New Event
      </button>
    </div>
  );
}

export default Timeline;
