// Board.js
import React, { useState } from 'react';
import Column from './Timeline';
import '../styles/style.css'; // Ensure your CSS file is imported

function Board() {
  const [columns, setColumns] = useState([
    { id: 'todo', title: 'To Do', cards: [] },
    { id: 'inprogress', title: 'In Progress', cards: [] },
    { id: 'done', title: 'Done', cards: [] }
  ]);

  const moveCard = (cardId, fromColumnId, toColumnId) => {
    const updatedColumns = columns.map((column) => {
      if (column.id === fromColumnId) {
        return {
          ...column,
          cards: column.cards.filter((card) => card.id !== cardId)
        };
      }
      if (column.id === toColumnId) {
        return {
          ...column,
          cards: [...column.cards, { id: cardId, content: `Task ${cardId}` }]
        };
      }
      return column;
    });
    setColumns(updatedColumns);
  };

  return (
    <div className="board">
      {columns.map((column) => (
        <Column key={column.id} column={column} moveCard={moveCard} />
      ))}
    </div>
  );
}

export default Board;
